import React from "react";
import web from "../src/images/it_building.jpg";
// import { NavLink } from "react-router-dom";
import Typical from 'react-typical';


const Home = () => {

return(
    <>
      <section id= "header" className="d-flex align-items-center" >
        <div className="container-fluid nav_bg" >
            <div className="row" >
                 <div className="col-10 mx-auto" >
                     <div className="row">
                     <div className="col-md-6 col-10 mb-1 pt-5 pt-lg-0 order-2 order-lg-1 d-flex justify-content-center flex-column text-center animated">
                         <h1> Painting </h1>
                          <h2 className="my-3 text-capitalize"> Join us<span>
                              <Typical
                                steps={['order now',5000,'order now',4000]}
                                loop={Infinity}
                                wrapper="h2"
                            />
                            </span>
                         </h2>
                         {/* <div className=" mt-3">
                             <NavLink to="/about" className="btn-visit"> Visit </NavLink>
                         </div> */}
                     </div>

                    <div className="col-lg-6 col-12 order-1 order-lg-2 header-img border">
                        <img src={web} className="img-fluid" alt="home img" />
                    </div>
                    </div>
                 </div>
             </div>
             <form className="form-inline ml-3 my-2 my-lg-0 col-6 col-lg-4 col-sm-12 d-flex flex-row p-1">
      <input className="form-control mr-2 col-6" type="text" placeholder="Search by Category" aria-label="Search" />
      <button className="btn btn-outline-success float-right my-2 my-sm-0 col-6 col-lg-8" type="submit">Search</button>
    </form>
       </div>
      </section>
      

    </>
);
};
export default Home;
