        <form onSubmit={onSubmit}>
        <div>
            <h1 style= {{textTransform:'capitalize'}}>Hello ! {FullName.fname} {FullName.lname} </h1>
                 <p> { FullName.email } </p>
                 <p> { FullName.phone } </p>
            <input type='text' 
            placeholder='Enter Your First Name' 
            name='fname'
            onChange ={inputEvent}
             value={FullName.fname}
             />

            <input type='text' 
            placeholder='Enter Your Last name'
            name='lname'
            onChange ={inputEvent}
             value={FullName.lname} 
