        setName(event.target.value);
    }

    const onSubmit = (event) =>{
        setFullName(name);
    }

    return(
        <>
        <div className='class_div'>
        <form onSubmit={onSubmit}>
        <div>
            <h1 style= {{textTransform:'capitalize'}}>Hello ! {fullName} </h1>
            <input type='text' 
            placeholder='Enter Your Name' 
            onChange ={inputEvent}
            value={name} />

            <input type='password' 
