const topFunction = () => {
  document.body.scrollTop = 0;
  document.documentElement.scrollTop = 0;
};


return(
    <>
        <div class="scrolltop float-right">
         	<ArrowUpwardIcon onclick={topFunction} id="myBtn" />
             </div>
    </>
);
};
export default Top;
