import React, { useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import CreateNote from "./CreateNote";
import Note from "./Note";
import "./Keep.css";

const Keep = () => {
    
    const [addItem, setAddItem] = useState([]);

    const addNote = (note) => {
        setAddItem((prevData) =>{
            return[...prevData,note];

        });
    };

    const onDelete = (id) =>{
        setAddItem((oldItems) => {
            return oldItems.filter((arrElem, index) => {
                return index !== id;
            })
        })
    };

return(
    <>
       <Header/>
       <CreateNote
          passNote={addNote}
       />

       {addItem.map((val, index)=>{
           return <Note 
             key ={ index }
             id={ index }
             title ={val.title}
             content={val.content}
             deleteItem={onDelete}
             />
       })}
       <Footer/>
    </>
);
};
export default Keep;
