import React, { useState } from "react";

const App = () => {

    let curTime= new Date().toLocaleTimeString();

    const[cTime ,setcTime] = useState(curTime);
    const UpdateTime = () =>{
        curTime= new Date().toLocaleTimeString();
        setcTime(curTime);
    }
     
    return(
        <>
        <div>
            <h1>{cTime}</h1>
            <button onClick = {UpdateTime}>Get Time</button>
         </div>
        </>
    );
};

export default App;
