    // const UpdateTime = () =>{
    //     curTime= new Date().toLocaleTimeString();
    //     setcTime(curTime);
    // }

    // setInterval(UpdateTime,1);
    const purple ='#8e44ad';
    const [bg,setBg] = useState(purple);

    const bgChange = () =>{
        let newBg = '#34495e';
        setBg(newBg);
    }; 

    return(
        <>
        <div style = {{backgroundColor : bg}}>
            <button onClick ={bgChange} >click me</button>
         </div>
        </>
    );
};

export default App;
