import React, { useState } from "react";

const Dtime = () => {

        let curTime= new Date().toLocaleTimeString();

    const[cTime ,setcTime] = useState(curTime);
    const UpdateTime = () =>{
        curTime= new Date().toLocaleTimeString();
        setcTime(curTime);
    }

    setInterval(UpdateTime,1);

    return (
        <>
          <h1> { curTime } </h1>
        
        </>
    );

};

export default Dtime;
