import React, { useState } from "react";

const App = () => {
   
    let curTime= new Date().toLocaleTimeString();

    const[cTime ,setcTime] = useState(curTime);
    const UpdateTime = () =>{
        curTime= new Date().toLocaleTimeString();
        setcTime(curTime);
    }

    setInterval(UpdateTime,1000);
     
    return(
        <>
        <div>
            <h1>{cTime}</h1>
         </div>
        </>
    );
};

export default App;
