import React, { useState } from "react";
import Header from "./Header";
import Footer from "./Footer";
import CreateNote from "./CreateNote";
import Note from "./Note";
import "./Keep.css";

const Keep = () => {
    
    const [addItem, setAddItem] = useState([]);

    const addNote = (note) => {
        setAddItem((prevData) =>{
            return[...prevData,note];

        })
    };

return(
    <>
       <Header/>
       <CreateNote
          passNote={addNote}
       />
       <Note/>
       <Footer/>
    </>
);
};
export default Keep;
